return __awaiter(this, void 0, void 0, function* () {
    let spawnCounter = 0;
    let eventCounter = 0;
    const device = new NodePyATVDevice({
        name: 'My Testdevice',
        host: '192.168.178.2',
        spawn: createFakeSpawn(cp => {
            if (spawnCounter === 0) {
                cp.onStdIn(() => cp.end());
            }
            cp.stdout({
                result: 'success',
                title: 'My Movie'
            });
            spawnCounter++;
            if (spawnCounter >= 2) {
                cp.end();
            }
        })
    });
    const listener = () => {
        eventCounter++;
    };
